{"agdmethod":"none","method":"ad_script","agdparams":"","id":0,"filters":[],"params":{"type":"powerShell","groovyscript":"<#
.SYNOPSIS
 This script will reach out to each Unitrends appliance configured in ADI and collect data to build a report. 
.NOTES
 Author: Andy Escolastico
#>
#----------------------------------------------------------[Declarations]----------------------------------------------------------#

$UniServer = "##hostname##"
$UniUser = "##uni.user##"
$UniPass = "##uni.pass##"

#-----------------------------------------------------------[Functions]------------------------------------------------------------#

(new-object Net.WebClient).DownloadString('https://raw.githubusercontent.com/RFAInc/rfa-backups/main/unitrends-functions.ps1') | Invoke-Expression

(new-object Net.WebClient).DownloadString('https://raw.githubusercontent.com/RFAInc/rfa-backups/main/general-functions.ps1') | Invoke-Expression

#-----------------------------------------------------------[Execution]------------------------------------------------------------#

# Ignores invalid certificate errors
Skip-InvalidCertErrors

try {
 # Connect to Unitrends server and retrieve auth token
 $AuthToken = (Get-UniSession -Server $UniServer -User $UniUser -Pass $UniPass).auth_token
 # Collect Job data
 $JobHistory = (Invoke-UniRest -Server $UniServer -AuthToken $AuthToken -Method "GET" -Endpoint "/jobs/history/backup").data
 $JobConfigs = (Invoke-UniRest -Server $UniServer -AuthToken $AuthToken -Method "GET" -Endpoint "/joborders").data
 # Add server ip and frequency datapoint to Job objects
 foreach ($i in $JobHistory) {
 $Frequency = $JobConfigs | Where-Object {$_.name -eq $i.name} | Select-Object calendar_str
 $Frequency = $Frequency.calendar_str | Where-Object {$_ -Notlike "*/*/*"}
 if (@($Frequency).Count -gt 1){
 $Frequency = $Frequency -Join "; "
 }
 $i | Add-Member -NotePropertyName "frequency" -NotePropertyValue $Frequency
 $i | Add-Member -NotePropertyName "system_ip" -NotePropertyValue $UniServer
 }
 # Collect Asset-Source data
 $Sources = (Invoke-UniRest -Server $UniServer -AuthToken $AuthToken -Method "GET" -Endpoint "/assets").data
 # Build Asset Custom Objects
 $Assets = @()
 foreach ($x in $Sources) {
 foreach ($y in $x.children){
 $obj = [PSCustomObject]@{
 Asset = $y.name
 Source = $x.name
 }
 $Assets += $obj
 }
 } 
} catch {
 Write-Output "Could not connect to $UniServer. Check Credentials or API access."
}

# Builds Objects for Unnasigned Assets
$arr = @()
foreach ($x in $JobHistory){
 foreach ($y in $x.backups){
 $obj = [PSCustomObject]@{
 Asset = $y.asset_name
 Source = $y.client_name
 Server = $x.system_ip
 Appliance = $x.system_name
 JobName = $x.name
 JobType = $x.type
 JobApp = $x.app_name
 JobFrequency = $x.frequency
 BackupStatus = $y.status
 BackupStart = [DateTime]$y.start_time
 BackupType = $y.mode
 BackupSize = $y.size
 BackupID = $y.backup_id
 }
 $arr += $obj
 } 
}
$AssignedUniAssets = $arr | Where-Object {($_.BackupStatus -ne "Active") -and ($_.BackupStatus -ne "Queued")} | Group-Object Asset | Foreach-Object {$_.Group | Sort-Object BackupStart | Select-Object -Last 1}
$ActiveUniAssets = $arr | Where-Object {($_.BackupStatus -eq "Active") -or ($_.BackupStatus -eq "Queued")}

# Builds Objects for Unnasigned Assets
$arr = @()
foreach ($i in $Assets) { 
 if ($i.Asset -notin $AssignedUniAssets.Asset) {
 $obj = [PSCustomObject]@{
 Asset = $i.Asset
 Source = $i.Source
 }
 $arr += $obj
 }
}
$UnassignedUniAssets = $arr

#-----------------------------------------------------------[LogicMonitor Output]------------------------------------------------------------#

# Discovery Output
foreach ($i in $AssignedUniAssets){
 Write-Output "$(($i.Asset) -replace(" ", "_"))##$($i.Asset)##$($i.Source)####auto.unitrends.BackupServer=$($i.Server)&auto.unitrends.BackupAppliance=$($i.Appliance)&auto.unitrends.JobName=$($i.JobName)&auto.unitrends.JobType=$($i.JobType)&auto.unitrends.JobApp=$($i.JobApp)&auto.unitrends.JobFrequency=$($i.JobFrequency)&auto.unitrends.BackupStart=$($i.BackupStart)&auto.unitrends.BackupType=$($i.BackupType)&auto.unitrends.BackupID=$($i.BackupID)"
}"}}
