def iterate(String path) {
    def response = []
    def remaining = true
    def offset = 0
    def size = 50
    while (remaining == true) {
        def result = (invokeLMAPI(path,'GET',"?size=${size}&offset=${offset}"))?.items
        result.each { item ->
            response.add(item)
        }
        if (result.size() < size) {
            remaining = false
        }
        else {
            offset += size
        }
    }
    return response
}
def invokeLMAPI(String path, String httpverb = 'GET', String queryParams = '', body = '') {

    // check if body is not a string, if it's not convert it
    if (body && !(body instanceof String)) {
        body = JsonOutput.toJson(body)
    }

    //Get hostname, port, url, and ensure httpverb is capitalized. 
    useSSL = true
    port = 443
    hostname = "${company}.logicmonitor.com"
    url = "https://${hostname}/santaba/rest${path}${queryParams}"
    httpverb = httpverb.toUpperCase()

    epoch = System.currentTimeMillis();
    requestVars = httpverb + epoch + body + path

    // construct signature
    hmac = Mac.getInstance("HmacSHA256");
    secret = new SecretKeySpec(accessKey.getBytes(), "HmacSHA256");
    hmac.init(secret);
    hmac_signed = Hex.encodeHexString(hmac.doFinal(requestVars.getBytes()));
    signature = hmac_signed.bytes.encodeBase64();

    //Construct those headerse baby
    headers = [
        'Authorization': "LMv1 ${accessId}:${signature}:${epoch}",
        'Content-Type': "application/json",
        'X-Version': 3
    ]

    //open httpClient
    httpClient = HTTP.open(hostname, port, useSSL)


    //Alrighty, we're going to make the request and loop if we get a 429 error, otherwise return the response/stats code. 
    stopLoop = false
    sleepTime = 10
    while (stopLoop == false) {

    //switch to decide what http call to make. 
    switch (httpverb) {
        case 'GET':
            httpGet = httpClient.get(url, headers)
            break
        case 'POST':
            httpPost = httpClient.post(url, body, headers)
            break
        case 'PUT':
            httpPut = httpClient.put(url, body, headers)
            break
        case 'PATCH':
            httpPatch = httpClient.patch(url, body, headers)
            break
        default:
            println 'Specify a correct httpverb please. e.g. GET, POST, PUT, PATCH'
            return null
            break

    }

    //Here is the status code
    statusCode = httpClient.getStatusCode()

    //switch to decide what to do with the status Code, possibly rate limit. 
    switch (statusCode) {
        case 200:
            response = new JsonSlurper().parseText(httpClient.getResponseBody())
            stopLoop = true
            break
        case 429:
            println "Request exceeded rate limit, retrying in ${sleepTime} seconds..."
            sleep(sleepTime * 1000)
            sleepTime = (sleepTime + 10)
            break
        default:
            stopLoop = true
            response = null
            println "Status code: ${statusCode}"
            println "See https://www.logicmonitor.com/support/rest-api-developers-guide/v2/ for more information."
        break
    }


    }
    return response
}
